import React, { useEffect, useState } from "react"
import styled from "styled-components"
import { Link } from "react-router-dom"
import { useRef } from "react"
import { ToastContainer, toast } from "react-toastify"
import "react-toastify/dist/ReactToastify.css"
import { url, urlList } from "../contants/url"
import { tadaData } from "../contants/addData"

const Container = styled.div`
  display: flex;
`
const WrapperToast = styled.div`
  position: fixed;
  top: 10px;
  right: 10px;
`
const Wrapper = styled.div`
  background-color: #fff;
  width: 30%;
  height: 300px;
  margin: 0 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`
const Item = styled.div`
  margin-bottom: 15px;
`
const Title = styled.label`
  display: block;
`
const Input = styled.input`
  width: 300px;
  height: 30px;
`
const Table = styled.table`
  padding: 0;
  width: 60%;
`
const Tr = styled.tr`
  border: 1px solid #fff;
`
const Th = styled.th`
  border: 1px solid #fff;
`
const Td = styled.td`
  border: 1px solid #fff;
`
const Button = styled.button`
  width: 300px;
  height: 40px;
  cursor: pointer;
`

const Home = () => {
  const [datas, setDatas] = useState([])
  const [postData, setPostData] = useState()
  const [count, setCount] = useState(0)
  const [fileName, setFileName] = useState("")
  const [abc, setAbc] = useState([])
  const index = useRef(0)

  useEffect(() => {
    fetch(urlList)
      .then((response) => response.json())
      .then((json) => {
        setDatas(json)
        // addManyData(json)
      })
      .catch((err) => console.log("Request Failed", err))
  }, [])

  useEffect(() => {
    for (const data of datas) {
      fetch(url + data)
        .then((response) => response.json())
        .then((json) => {
          addData(json)
          getData()
        })
        .catch((err) => {
          console.log("Request Failed", err)
        })
    }
  }, [datas])

  const getData = () => {
    const indexedDB = window.indexedDB
    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error", e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      const store = db.createObjectStore("cars", { keyPath: "id" })
      store.createIndex("cars_colour", ["colour"], { unique: false })
      store.createIndex("cars_colour_make", ["colour", "make"], {
        unique: false,
      })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")
      const store = transaction.objectStore("cars")

      const idQuery = store.getAll()
      idQuery.onsuccess = function () {
        // console.log("idQuery.result", idQuery.result)
        setAbc(idQuery.result)
      }
      transaction.oncomplete = function () {
        db.close()
      }
    }
  }

  const backgroundSyncAdd = () => {
    navigator.serviceWorker.ready.then((swRegistration) =>
      swRegistration.sync.register("post-data").catch((err) => console.log(err))
    )
  }

  const addData = (data) => {
    const indexedDB = window.indexedDB

    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error")
      console.error(e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      const store = db.createObjectStore("cars", { keyPath: "id" })
      store.createIndex("cars_colour", ["colour"], { unique: false })
      store.createIndex("cars_colour_make", ["colour", "make"], {
        unique: false,
      })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")
      const store = transaction.objectStore("cars")
      store.put({
        id: data.audioID,
        colour: data,
        make: data,
        delete: false,
      })
      // store.put({ id: 1, colour: postData.name, make: postData.email })
      // store.put({ id: index.current + 2, colour: "red", make: "kia" })

      transaction.oncomplete = function () {
        db.close()
      }
    }
  }
  const addManyData = (data) => {
    const indexedDB = window.indexedDB

    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error")
      console.error(e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      const store = db.createObjectStore("cars", { keyPath: "id" })
      store.createIndex("cars_colour", ["colour"], { unique: false })
      store.createIndex("cars_colour_make", ["colour", "make"], {
        unique: false,
      })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")

      const store = transaction.objectStore("cars")
      data.map((item) =>
        // store.put({
        //   id: item.audioID,
        //   colour: item.audioID,
        //   make: item.audioID,
        // })
        store.put({
          id: item,
          colour: item,
          make: item,
        })
      )
      // store.put({
      //   id: index.current,
      //   colour: data.name,
      //   make: data.email,
      //   delete: data.delete,
      // })

      transaction.oncomplete = function () {
        db.close()
      }
