import React, { useEffect, useState } from "react"
import styled from "styled-components"
import { Link } from "react-router-dom"
import { useRef } from "react"
import { ToastContainer, toast } from "react-toastify"
import "react-toastify/dist/ReactToastify.css"
import { url, urlList } from "../contants/url"

const Container = styled.div`
  display: flex;
`
const WrapperToast = styled.div`
  position: fixed;
  top: 10px;
  right: 10px;
`
const Wrapper = styled.div`
  background-color: #fff;
  width: 30%;
  height: 300px;
  margin: 0 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`
const Item = styled.div`
  margin-bottom: 15px;
`
const Title = styled.label`
  display: block;
`
const Input = styled.input`
  width: 300px;
  height: 30px;
`
const Table = styled.table`
  padding: 0;
  width: 60%;
`
const Tr = styled.tr`
  border: 1px solid #fff;
`
const Th = styled.th`
  border: 1px solid #fff;
`
const Td = styled.td`
  border: 1px solid #fff;
`
const Button = styled.button`
  width: 300px;
  height: 40px;
  cursor: pointer;
`

const Home = () => {
  const tadaData = (data) => {
    return {
      audioID: data,
      audioCredit: {
        fileURL: `talk${data}.mp3`,
        type: "audio",
        agent: {
          agentID: data,
          name: "Đặng Thị An",
          cty: "ABC",
          hotline: 35424786,
        },
        customer: {
          customerID: data,
          name: "Phạm Hữu Thái Qúy",
          phone: 69256624,
          address: "Hà Nội",
          cmnd: 25400346,
        },
        callHoldDration: "3s",
        callDuration: "382s",
        date: "1/9/2022",
        time_start: "10:40:20",
      },
      audioText: [
        {
          voiceLayer: {
            voiceNumber: 2,
            content: "thì anh đi chứ anh đâu biết đâu",
            duration: "00:00:00",
          },
        },
        {
          voiceLayer: {
            voiceNumber: 2,
            content: "rồi anh đợi em đăng ký chính chủ chưa",
            duration: "00:00:11",
          },
        },
        {
          voiceLayer: {
            voiceNumber: 2,
            content: "nó được tháng ròi",
            duration: "00:00:22",
          },
        },
        {
          voiceLayer: {
            voiceNumber: 1,
            content: "nó được tháng ròi",
            duration: "00:00:33",
          },
        },
        {
          voiceLayer: { voiceNumber: 2, content: "đợi", duration: "00:00:44" },
        },
        {
          voiceLayer: {
            voiceNumber: 1,
            content: "em một xíu",
            duration: "00:00:55",
          },
        },
        {
          voiceLayer: {
            voiceNumber: 2,
            content: "dạ anh đang có vay bênh bênh em ạ",
            duration: "00:00:66",
          },
        },
        {
          voiceLayer: {
            voiceNumber: 2,
            content: "xin chào",
            duration: "00:00:77",
          },
        },
        {
          voiceLayer: {
            voiceNumber: 2,
            content: "dạ anh đang có vay bênh bênh em ạ",
            duration: "00:00:88",
          },
        },
        { voiceLayer: { voiceNumber: 2, content: "ừ", duration: "00:00:99" } },
      ],
      audioMatch: {
        rules: [
          {
            name: "BR_Wrongnumber",
            key: [{ agent: "hết hạn", customer: "anh đâu biết đâu" }],
          },
          {
            name: "BR_Wrongnumber",
            key: [{ agent: "hết hạn", customer: "đâu biết" }],
          },
          {
            name: "BR_Wrongnumber",
            key: [{ agent: "nợ chưa trả", customer: "đâu biết" }],
          },
          {
            name: "CollAgentScriptAdherence",
            key: [{ agent: "hết hạn", customer: "đâu biết" }],
          },
          {
            name: "BR_Wrongnumber",
            key: [{ agent: "số tiền", customer: "đâu biết" }],
          },
          {
            name: "BR_Wrongnumber",
            key: [
              { agent: "sao anh chưa thanh toán", customer: "anh đâu biết" },
            ],
          },
        ],
      },
    }
  }
  const [datas, setDatas] = useState([])
  const [postData, setPostData] = useState()
  const [count, setCount] = useState(0)
  const [fileName, setFileName] = useState("")
  const index = useRef(0)

  useEffect(() => {
    fetch(urlList)
      .then((response) => response.json())
      .then((json) => {
        setDatas(json)
        addManyData(json)
      })
      .catch((err) => console.log("Request Failed", err))
  }, [count, postData])

  const backgroundSyncAdd = () => {
    navigator.serviceWorker.ready.then((swRegistration) =>
      swRegistration.sync.register("post-data").catch((err) => console.log(err))
    )
  }

  const addData = (data) => {
    const indexedDB = window.indexedDB

    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error")
      console.error(e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      const store = db.createObjectStore("cars", { keyPath: "id" })
      store.createIndex("cars_colour", ["colour"], { unique: false })
      store.createIndex("cars_colour_make", ["colour", "make"], {
        unique: false,
      })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")

      const store = transaction.objectStore("cars")

      store.put({
        id: index.current,
        colour: data.name,
        make: data.email,
        delete: data.delete,
      })
      // store.put({ id: 1, colour: postData.name, make: postData.email })
      // store.put({ id: index.current + 2, colour: "red", make: "kia" })

      transaction.oncomplete = function () {
        db.close()
      }
    }
  }
  const addManyData = (data) => {
    const indexedDB = window.indexedDB

    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error")
      console.error(e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      const store = db.createObjectStore("cars", { keyPath: "id" })
      store.createIndex("cars_colour", ["colour"], { unique: false })
      store.createIndex("cars_colour_make", ["colour", "make"], {
        unique: false,
      })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")

      const store = transaction.objectStore("cars")
      data.map((item) =>
        // store.put({
        //   id: item.audioID,
        //   colour: item.audioID,
        //   make: item.audioID,
        // })
        store.put({
          id: item,
          colour: item,
          make: item,
        })
      )
      // store.put({
      //   id: index.current,
      //   colour: data.name,
      //   make: data.email,
      //   delete: data.delete,
      // })

      transaction.oncomplete = function () {
        db.close()
      }
    }
  }
  const sendAddData = (data) => {
    fetch(url + fileName, {
      method: "POST",
      body: JSON.stringify(tadaData(fileName)),
      mode: "no-cors",
      headers: { "Content-type": "application/json; charset=UTF-8" },
    })
      .then((response) => {
        setCount(count + 1)
        setFileName("")
        toast("Thêm dữ liệu thành công!")
