import React, { useEffect, useState } from "react"
import styled from "styled-components"
import { Link } from "react-router-dom"
import FILE1 from "../components/files/file1"
import axios from "axios"
import { useRef } from "react"
import { ToastContainer, toast } from "react-toastify"
import "react-toastify/dist/ReactToastify.css"

const Container = styled.div`
  display: flex;
`
const WrapperToast = styled.div`
  position: fixed;
  top: 10px;
  right: 10px;
`
const Wrapper = styled.div`
  background-color: #fff;
  width: 30%;
  height: 300px;
  margin: 0 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`
const Item = styled.div`
  margin-bottom: 15px;
`
const Title = styled.label`
  display: block;
`
const Input = styled.input`
  width: 300px;
  height: 30px;
`
const Table = styled.table`
  padding: 0;
  width: 60%;
`
const Tr = styled.tr`
  border: 1px solid #fff;
`
const Th = styled.th`
  border: 1px solid #fff;
`
const Td = styled.td`
  border: 1px solid #fff;
`
const Button = styled.button`
  width: 300px;
  height: 40px;
  cursor: pointer;
`
const Home = () => {
  const [datas, setDatas] = useState([])
  const [postData, setPostData] = useState({})
  const [count, setCount] = useState(0)

  const index = useRef(0)
  // const url = "https://calls-anlytics.devops-cloudflare.workers.dev/api"
  const urlList = "https://r2-call-analytics.devops-cloudflare.workers.dev/list"
  const url = "https://r2-call-analytics.devops-cloudflare.workers.dev/"
  useEffect(() => {
    fetch(urlList)
      .then((response) => response.json())
      .then((json) => {
        setDatas(json)
        addManyData(json)
      })
      .catch((err) => console.log("Request Failed", err))
  }, [count, postData])

  const backgroundSyncAdd = () => {
    navigator.serviceWorker.ready.then((swRegistration) =>
      swRegistration.sync.register("post-data").catch((err) => console.log(err))
    )
  }

  const addData = (data) => {
    const indexedDB = window.indexedDB

    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error")
      console.error(e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      const store = db.createObjectStore("cars", { keyPath: "id" })
      store.createIndex("cars_colour", ["colour"], { unique: false })
      store.createIndex("cars_colour_make", ["colour", "make"], {
        unique: false,
      })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")

      const store = transaction.objectStore("cars")

      store.put({
        id: index.current,
        colour: data.name,
        make: data.email,
        delete: data.delete,
      })
      // store.put({ id: 1, colour: postData.name, make: postData.email })
      // store.put({ id: index.current + 2, colour: "red", make: "kia" })

      transaction.oncomplete = function () {
        db.close()
      }
    }
  }
  const addManyData = (data) => {
    const indexedDB = window.indexedDB

    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error")
      console.error(e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      const store = db.createObjectStore("cars", { keyPath: "id" })
      store.createIndex("cars_colour", ["colour"], { unique: false })
      store.createIndex("cars_colour_make", ["colour", "make"], {
        unique: false,
      })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")

      const store = transaction.objectStore("cars")
      data.map((item) =>
        // store.put({
        //   id: item.audioID,
        //   colour: item.audioID,
        //   make: item.audioID,
        // })
        store.put({
          id: item,
          colour: item,
          make: item,
        })
      )
      // store.put({
      //   id: index.current,
      //   colour: data.name,
      //   make: data.email,
      //   delete: data.delete,
      // })

      transaction.oncomplete = function () {
        db.close()
      }
    }
  }
  const sendAddData = (data) => {
    fetch(url, {
      method: "POST",
      body: JSON.stringify(data),
      headers: { "Content-type": "application/json; charset=UTF-8" },
    })
      .then((response) => {
        setCount(count + 1)
        toast("Thêm dữ liệu thành công!")
        response.json()
      })
      .then((json) => console.log(json))
      .catch((err) => {
        console.log(err)
        addData(data)
        backgroundSyncAdd()
        toast("Thêm dữ liệu thành công!")
        setDatas((prev) => [...prev, { _id: index.current, ...data }])
      })
  }

  const handleAdd = () => {
    index.current += 1
    sendAddData(postData)
    setCount((prev) => prev + 1)
    setCount((prev) => prev + 1)
    setCount((prev) => prev + 1)
    setCount((prev) => prev + 1)
    setTimeout(() => {
      setCount((prev) => prev + 1)
    }, 5000)
    setPostData({ name: "", email: "", content: "", delete: false })
  }
  /////////////////////////////////////////////////////////

  const backgroundSyncDelete = () => {
    navigator.serviceWorker.ready.then((swRegistration) =>
      swRegistration.sync
        .register("delete-data")
        .catch((err) => console.log(err))
    )
  }

  const deleteData = (data) => {
    const indexedDB = window.indexedDB

    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error")
      console.error(e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      const store = db.createObjectStore("cars", { keyPath: "id" })
      store.createIndex("cars_colour", ["colour"], { unique: false })
      store.createIndex("cars_colour_make", ["colour", "make"], {
        unique: false,
      })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")

      const store = transaction.objectStore("cars")
      const makeModeIndex = store.index("cars_colour_make")
      const colourMakeQuery = makeModeIndex.get([data.audioID, data.audioID])
      colourMakeQuery.onsuccess = function () {
        if (colourMakeQuery.result) {
          console.log("colourMakeQuery.result", colourMakeQuery.result)
          console.log(
            "colourMakeQuery.result.delete",
            colourMakeQuery.result.delete
          )
          colourMakeQuery.result.delete = true
          // const updateRequest = store.update(colourMakeQuery.result)
          const updateRequest = store.put(colourMakeQuery.result)
          updateRequest.onsuccess = function () {
            console.log("update success result", colourMakeQuery.result)
            console.log("update success", colourMakeQuery.result.delete)
          }
        } else {
          const indexedDB = window.indexedDB

          const request = indexedDB.open("deletecars", 1)
          request.onerror = function (e) {
            console.error("error")
            console.error(e)
          }
          request.onupgradeneeded = function () {
            const db = request.result
            const store = db.createObjectStore("cars", { keyPath: "id" })
            store.createIndex("cars_colour", ["colour"], { unique: false })
            store.createIndex("cars_colour_make", ["colour", "make"], {
              unique: false,
            })
          }
          request.onsuccess = function () {
            const db = request.result
            const transaction = db.transaction("cars", "readwrite")

            const store = transaction.objectStore("cars")

            store.put({
              id: data._id,
              colour: data.name,
              make: data.email,
            })
            // store.put({ id: 1, colour: postData.name, make: postData.email })
            // store.put({ id: index.current + 2, colour: "red", make: "kia" })

            transaction.oncomplete = function () {
              db.close()
            }
          }
        }
      }

      transaction.oncomplete = function () {
        db.close()
      }
    }
  }

  const sendDeleteData = (data) => {
    fetch(url + data, {
      method: "PUT",
      headers: {
        mode: "cors", // no-cors, *cors, same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
          "Content-Type": "application/json",
          // "Content-Type": "application/x-www-form-urlencoded",
        },
      },
    })
      .then((response) => {
        setCount((prev) => prev + 1)
        toast("Xóa dữ liệu thành công!")
        response.json()
      })
      .then((json) => console.log(json))
      .catch((err) => {
        console.log(err)
        deleteData(data)
