import React, { useEffect, useState } from "react"
import styled from "styled-components"
import Alerts from "../components/Alerts"
import Audio from "../components/Audio"
import ContactDetails from "../components/ContactDetails"
import Conversation from "../components/Conversation"
import FILE1 from "../components/files/file1"
import Metadata from "../components/Metadata"
import Phrases from "../components/Phrases"
import Scores from "../components/Scores"

const Top = styled.div`
  display: grid;
  grid-template-columns: 3fr 2fr 4fr;
  grid-gap: 20px;
  margin-bottom: 20px;
  @media screen and (max-width: 576px) {
    display: flex;
    flex-direction: column;
  }
`
const Bottom = styled.div``
const Detail = () => {
  const [data, setData] = useState({})
  // useEffect(() => {
  //   if (window.location.pathname.split("=")[1] < 100) {
  //     setData(
  //       FILE1.find(
  //         (item) => item.audioID == window.location.pathname.split("=")[1]
  //       )
  //     )
  //   }
  // }, [])
  window.location.pathname.slice(1)
  const itemID = window.location.pathname.split("=")[1]
  // useEffect(() => {
  //   fetch(`https://r2-call-analytics.devops-cloudflare.workers.dev/${itemID}`)
  //     .then((response) => response.json())
  //     .then((json) => {
  //       setData(json)
  //     })
  //     .catch((err) => console.log("Request Failed", err))
  // }, [itemID])

  const getData = (key) => {
    const indexedDB = window.indexedDB
    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error", e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      const store = db.createObjectStore("cars", { keyPath: "id" })
      store.createIndex("cars_colour", ["colour"], { unique: false })
      store.createIndex("cars_colour_make", ["colour", "make"], {
        unique: false,
      })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")
      const store = transaction.objectStore("cars")
      const idQuery = store.getAll(key)
      idQuery.onsuccess = function () {
        console.log("idQuery.result", idQuery.result)
        setData(idQuery.result)
      }
      transaction.oncomplete = function () {
        db.close()
      }
    }
  }
  console.log(getData(itemID))
  console.log(data)
  return (
    <>
      {/* <Top>
        <ContactDetails data={data !== undefined && data.colour} />
        <Metadata data={data.colour} />
        <Audio />
      </Top>
      <Top>
        <Scores />
        <Alerts data={data.colour} />
        <Phrases data={data.colour} />
      </Top>
      <Bottom>
        <Conversation data={data.colour} />
      </Bottom> */}
    </>
  )
}

export default Detail
