import React, { useEffect, useState } from "react"
import styled from "styled-components"
import { Link } from "react-router-dom"
import { useRef } from "react"
import { ToastContainer, toast } from "react-toastify"
import "react-toastify/dist/ReactToastify.css"
import { url, urlList } from "../contants/url"
import { tadaData } from "../contants/addData"

const Container = styled.div`
  display: flex;
`
const WrapperToast = styled.div`
  position: fixed;
  top: 10px;
  right: 10px;
`
const Wrapper = styled.div`
  background-color: #fff;
  width: 30%;
  height: 300px;
  margin: 0 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`
const Item = styled.div`
  margin-bottom: 15px;
`
const Title = styled.label`
  display: block;
`
const Input = styled.input`
  width: 300px;
  height: 30px;
`
const Table = styled.table`
  padding: 0;
  width: 60%;
`
const Tr = styled.tr`
  border: 1px solid #fff;
`
const Th = styled.th`
  border: 1px solid #fff;
`
const Td = styled.td`
  border: 1px solid #fff;
`
const Button = styled.button`
  width: 300px;
  height: 40px;
  cursor: pointer;
`

const Home = () => {
  const [listAudioID, setListAudioID] = useState([])
  const [postData, setPostData] = useState()
  const [count, setCount] = useState(0)
  const [fileName, setFileName] = useState("")
  const [abc, setAbc] = useState([])
  const [listData, setListData] = useState([])
  const index = useRef(0)

  useEffect(() => {
    fetch(urlList)
      .then((response) => response.json())
      .then((json) => {
        setListAudioID(json)
      })
      .catch((err) => {
        getAllDataIndexDB()
        console.log("Request Failed", err)
      })
  }, [])

  useEffect(() => {
    for (const data of listAudioID) {
      fetch(url + data)
        .then((response) => response.json())
        .then((json) => {
          // getData()
          addManyDataIndexDB(json)
        })
        .catch((err) => {
          getAllDataIndexDB()
          console.log("Request Failed", err)
        })
    }
  }, [listAudioID])

  const getAllDataIndexDB = () => {
    const indexedDB = window.indexedDB
    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error", e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      db.createObjectStore("cars", { keyPath: "id" })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")
      const store = transaction.objectStore("cars")

      const getAllDataIndexDB = store.getAll()
      getAllDataIndexDB.onsuccess = function () {
        console.log("getAllDataIndexDB.result", getAllDataIndexDB.result)
        setListData(getAllDataIndexDB.result)
      }
      transaction.oncomplete = function () {
        db.close()
      }
    }
  }

  // const backgroundSyncAdd = () => {
  //   navigator.serviceWorker.ready.then((swRegistration) =>
  //     swRegistration.sync.register("post-data").catch((err) => console.log(err))
  //   )
  // }

  const addData = (data) => {
    const indexedDB = window.indexedDB

    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error")
      console.error(e)
    }
    request.onupgradeneeded = function () {
      const db = request.result
      const store = db.createObjectStore("cars", { keyPath: "id" })
      store.createIndex("cars_colour", ["colour"], { unique: false })
      store.createIndex("cars_colour_make", ["colour", "make"], {
        unique: false,
      })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")
      const store = transaction.objectStore("cars")
      store.put({
        id: data.audioID,
        data: data,
      })

      transaction.oncomplete = function () {
        db.close()
      }
    }
  }
  const addManyDataIndexDB = (data) => {
    const indexedDB = window.indexedDB

    const request = indexedDB.open("cars", 1)
    request.onerror = function (e) {
      console.error("error")
    }
    request.onupgradeneeded = function () {
      const db = request.result
      db.createObjectStore("cars", { keyPath: "id", autoIncrement: true })
    }
    request.onsuccess = function () {
      const db = request.result
      const transaction = db.transaction("cars", "readwrite")

      const store = transaction.objectStore("cars")
      store.put({
        id: data.audioID,
        data: data,
      })
      const getAllDataIndexDB = store.getAll()
      getAllDataIndexDB.onsuccess = function () {
        setListData(getAllDataIndexDB.result)
      }
      transaction.oncomplete = function () {
        db.close()
      }
    }
  }

  console.log("listData", listData)
  const sendDataServer = (data) => {
    console.log("data", data)
    fetch(url + fileName, {
      method: "POST",
      body: JSON.stringify(data),
      mode: "no-cors",
      headers: { "Content-type": "application/json; charset=UTF-8" },
    })
      .then((response) => {
        setFileName("")
        addData(data)
        setListAudioID((prev) => [...prev, data])
        toast("Thêm dữ liệu thành công!")
        response.json()
      })
      .then((json) => console.log(json))
      .catch((err) => {
        console.log(err)
        // addData(data)
        // backgroundSyncAdd()
        toast("Thêm dữ liệu thành công!")
        setListAudioID((prev) => [...prev, data])
      })
  }

  const handleAdd = () => {
    // index.current += 1
    // sendDataServer(postData)
    sendDataServer(tadaData(fileName))
    // setCount((prev) => prev + 1)
    // setPostData({ name: "", email: "", content: "", delete: false })
  }
  /////////////////////////////////////////////////////////

  // const backgroundSyncDelete = () => {
  //   navigator.serviceWorker.ready.then((swRegistration) =>
  //     swRegistration.sync
  //       .register("delete-data")
  //       .catch((err) => console.log(err))
  //   )
  // }

  // const deleteData = (data) => {
  //   const indexedDB = window.indexedDB

  //   const request = indexedDB.open("cars", 1)
  //   request.onerror = function (e) {
  //     console.error("error")
  //     console.error(e)
  //   }
  //   request.onupgradeneeded = function () {
  //     const db = request.result
  //     const store = db.createObjectStore("cars", { keyPath: "id" })
  //     store.createIndex("cars_colour", ["colour"], { unique: false })
  //     store.createIndex("cars_colour_make", ["colour", "make"], {
  //       unique: false,
  //     })
  //   }
  //   request.onsuccess = function () {
  //     const db = request.result
  //     const transaction = db.transaction("cars", "readwrite")

